        {
            "$sort": {
                "time": -1
            }
        },

        // Project the fields we want to keep
        {
            "$project": {
                "_id": 0,
                "time": 1,
                "batt": "$batt"
            }
        }
    ]
    )

db.renogy_solar_charger.aggregate(
[
    {
        // Convert the timestamp to a date object
        "$set": {
            "timestamp": {
                "$toDate": "$timestamp"
            }
        }
    },
    {
        $project: {
            _id: 0,
            timestamp: 1,
            batterylow: { // Possible values: deactivated, floating, boost
                // Need to map to 0, 1, 2
                $cond: {
                    if: { $eq: ["$batterylow", "deactivated"] },
                    then: 0,
                    else: {
                        $cond: {
                            if: { $eq: ["$batterylow", "floating"] },
                            then: 1,
                            else: 2
                        }
                    }
                }
            }
        }
    }
]
)

